mapper.select:
SELECT s.pno pno, s.category category, s.userid userid, s.title title, s.price price, s.delivery delivery, s.detail detail, s.keyword keyword, s.regdate regdate, s.updatedate updatedate, sf.fileName fileName
FROM S_product s LEFT JOIN S_product_file sf ON s.pno = sf.pno ORDER BY pno DESC
LIMIT #{from}, #{amount}

SELECT s.pno pno, s.category category, s.userid userid, s.title title, s.price price, s.delivery delivery, s.detail detail, s.keyword keyword, s.regdate regdate, s.updatedate updatedate, sf.fileName fileName
FROM S_product s LEFT JOIN S_product_file sf ON s.pno = sf.pno WHERE s.category = '가구' ORDER BY pno DESC
LIMIT #{from}, #{amount}
